import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, ScrollView, TextInput } from 'react-native';
import { LineChart } from 'react-native-svg-charts'; // Import the LineChart component
import * as shape from 'd3-shape';

const ProfileScreen = () => {
  const [report, setReport] = useState('');
  const [reports, setReports] = useState([]);
  const [attendanceData, setAttendanceData] = useState([50, 70, 80, 90, 75]); // Example attendance data
  const [editIndex, setEditIndex] = useState(null);

  const handleAddReport = () => {
    if (report.trim() !== '') {
      if (editIndex !== null) {
        const updatedReports = [...reports];
        updatedReports[editIndex] = report;
        setReports(updatedReports);
        setEditIndex(null);
      } else {
        setReports([...reports, report]);
      }

      setReport('');
    }
  };

  const handleEditReport = (index) => {
    setReport(reports[index]);
    setEditIndex(index);
  };

  const handleDeleteReport = (index) => {
    const updatedReports = [...reports];
    updatedReports.splice(index, 1);
    setReports(updatedReports);
  };

  useEffect(() => {
    // Simulate real-time attendance data update
    const interval = setInterval(() => {
      const newAttendanceValue = Math.floor(Math.random() * 100);
      setAttendanceData([...attendanceData.slice(1), newAttendanceValue]);
    }, 2000);

    return () => clearInterval(interval);
  }, [attendanceData]);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <TouchableOpacity style={styles.circularShape}>
        <Text style={styles.initials}>JD</Text>
      </TouchableOpacity>

      <View style={styles.infoContainer}>
        {/* Your profile information */}
      </View>

      <View style={styles.reportContainer}>
        <Text style={styles.reportLabel}>Report:</Text>
        <TextInput
          style={styles.reportInput}
          multiline
          placeholder="Write a report..."
          value={report}
          onChangeText={(text) => setReport(text)}
        />
        <TouchableOpacity style={styles.addButton} onPress={handleAddReport}>
          <Text style={styles.addButtonText}>
            {editIndex !== null ? 'Update Report' : 'Add Report'}
          </Text>
        </TouchableOpacity>
      </View>

      {/* Display Reports */}
      <View style={styles.reportsContainer}>
        <Text style={styles.reportsLabel}>Reports:</Text>
        {reports.map((item, index) => (
          <View key={index} style={styles.reportItem}>
            <Text style={styles.reportText}>{item}</Text>
            <TouchableOpacity
              style={styles.editButton}
              onPress={() => handleEditReport(index)}
            >
              <Text style={styles.editButtonText}>Edit</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.deleteButton}
              onPress={() => handleDeleteReport(index)}
            >
              <Text style={styles.deleteButtonText}>Delete</Text>
            </TouchableOpacity>
          </View>
        ))}
      </View>

      {/* Display the real-time attendance graph */}
      <View style={styles.attendanceGraphContainer}>
        <Text style={styles.attendanceLabel}>Attendance Over Time</Text>
        <LineChart
          style={{ height: 200 }}
          data={attendanceData}
          contentInset={{ top: 20, bottom: 20 }}
          curve={shape.curveNatural}
          svg={{ stroke: '#458a0a' }} // Line color
        />
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  // Your existing styles

  attendanceGraphContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  attendanceLabel: {
    fontSize: 20,
    fontWeight: 'bold',
  },
});

export default ProfileScreen;
