import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Image } from 'react-native';

import Icon from 'react-native-vector-icons/FontAwesome';

export default function TeacherDashboard() {
  return (
    <View style={styles.container}>
      {/* Navigation bar */}
      <View style={styles.navigationBar}>
        <Text style={styles.navTitle}>Teacher Dashboard</Text>
        <TouchableOpacity style={styles.navIcon} onPress={() => { /* Handle navigation menu */ }}>
          <Icon name="bars" size={24} color="white" />
        </TouchableOpacity>
      </View>

      {/* Content */}
      <ScrollView style={styles.content}>
        {/* Students Panel */}
        <View style={styles.panel}>
          <Text style={styles.panelTitle}>Students</Text>
          <Text style={styles.panelSubtitle}>Manage 50 students</Text>
          {/* List of students or any student-related information */}
        </View>

        {/* Classes Panel */}
        <View style={styles.panel}>
          <Text style={styles.panelTitle}>Classes</Text>
          <Text style={styles.panelSubtitle}>Teach 3 classes</Text>
          {/* List of classes or any class-related information */}
        </View>

        {/* Assignments Panel */}
        <View style={styles.panel}>
          <Text style={styles.panelTitle}>Assignments</Text>
          {/* List of assignments or any assignment-related information */}
        </View>

        {/* Schedule Panel */}
        <View style={styles.panel}>
          <Text style={styles.panelTitle}>Schedule</Text>
          {/* Weekly schedule or any schedule-related information */}
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  navigationBar: {
    backgroundColor: '#007AFF', // Your navigation bar color
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
  },
  navTitle: {
    fontSize: 24,
    color: 'white',
  },
  navIcon: {
    backgroundColor: 'transparent',
  },
  content: {
    flex: 1,
    padding: 10,
  },
  panel: {
    backgroundColor: '#fff', // Panel background color
    borderRadius: 8,
    padding: 15,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
  },
  panelTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  panelSubtitle: {
    fontSize: 16,
    color: '#888',
    marginBottom: 10,
  },
});
