import React, { useState, useEffect } from 'react';
import { View, Text, Button, FlatList } from 'react-native';
import { PieChart } from 'react-native-chart-kit';

const AttendanceScreen = () => {
  const [students1, setStudents1] = useState([
    { name: 'Student 1', studentNumber: '12345', isPresent: false },
    { name: 'Student 3', studentNumber: '54321', isPresent: false },
    { name: 'Student 4', studentNumber: '98765', isPresent: false },
    { name: 'Student 5', studentNumber: '13579', isPresent: false },
  ]);

  useEffect(() => {
    // Calculate attendance data whenever the student list changes
    const calculateAttendance = (studentList) => {
      let presentCount = 0;
      let absentCount = 0;

      studentList.forEach((student) => {
        if (student.isPresent) {
          presentCount++;
        } else {
          absentCount++;
        }
      });

      return [{ name: 'Present', count: presentCount }, { name: 'Absent', count: absentCount }];
    };

    const attendanceData = calculateAttendance(students1);

    // Set the attendance data in the state
    setAttendanceData1(attendanceData);
  }, [students1]);

  // Define custom colors for the pie chart segments
  const chartColors = ['#FF5733', '#33FF57'];

  const markAttendance = (index, studentList, setStudentList) => {
    const updatedStudents = [...studentList];
    updatedStudents[index].isPresent = !updatedStudents[index].isPresent;
    setStudentList(updatedStudents);
  };

  const renderItem = ({ item, index, studentList, setStudentList }) => (
    <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', padding: 10 }}>
      <View style={{ flex: 2, borderWidth: 1, borderColor: '#ccc', padding: 10 }}>
        <Text>{item.name}</Text>
      </View>
      <View style={{ flex: 1, borderWidth: 1, borderColor: '#ccc', padding: 10 }}>
        <Text>{item.studentNumber}</Text>
      </View>
      <View style={{ flex: 1, borderWidth: 1, borderColor: '#ccc', padding: 10 }}>
        <Button
          title={item.isPresent ? 'Present' : 'Absent'}
          onPress={() => markAttendance(index, studentList, setStudentList)}
        />
      </View>
    </View>
  );

  return (
    <View style={{ flex: 1 }}>
      <View style={{ backgroundColor: '#fff' }}>
        <Text style={{ textAlign: 'center', fontSize: 20, fontWeight: 'bold', marginVertical: 10 }}>FA1-BSIT03-WEBDEV2</Text>
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', padding: 10, backgroundColor: '#eee' }}>
          <View style={{ flex: 2 }}>
            <Text style={{ fontWeight: 'bold' }}>Name</Text>
          </View>
          <View style={{ flex: 1 }}>
            <Text style={{ fontWeight: 'bold' }}>ID Number</Text>
          </View>
          <View style={{ flex: 1 }}>
            <Text style={{ fontWeight: 'bold' }}>Attendance</Text>
          </View>
        </View>
        <FlatList
          data={students1}
          renderItem={({ item, index }) => renderItem({ item, index, studentList: students1, setStudentList: setStudents1 })}
          keyExtractor={(item, index) => index.toString()}
        />
      </View>

      {/* Attendance Chart (Pie Chart) */}
      <View style={{ marginVertical: 20 }}>
        <Text style={{ textAlign: 'center', fontSize: 20, fontWeight: 'bold' }}>Attendance Chart</Text>
        <PieChart
          data={attendanceData1}
          width={Dimensions.get('window').width - 40}
          height={200}
          chartConfig={{
            backgroundGradientFrom: 'white',
            backgroundGradientTo: 'white',
            color: (opacity = 1) => chartColors.map((color) => `${color}ff`),
          }}
          accessor="count"
          backgroundColor="transparent"
          paddingLeft="15"
        />
      </View>
    </View>
  );
};

export default AttendanceScreen;
